document.addEventListener("DOMContentLoaded", function () {
    const menuItems = document.querySelectorAll(".menu");
    let hideTimeout; // Timeout variable to delay hiding

    menuItems.forEach((menu) => {
        const megaMenu = menu.querySelector(".mega-menu");

        // Jab user menu par aaye toh mega menu show ho
        menu.addEventListener("mouseenter", function () {
            clearTimeout(hideTimeout); // Hide timeout clear kardo
            megaMenu.style.display = "flex";
        });

        // Jab user mega menu par aaye toh bhi show rehna chahiye
        megaMenu.addEventListener("mouseenter", function () {
            clearTimeout(hideTimeout);
            megaMenu.style.display = "flex";
        });

        // Jab user menu ya mega menu se bahar jaye toh delay ke baad hide ho
        menu.addEventListener("mouseleave", function () {
            hideTimeout = setTimeout(() => {
                megaMenu.style.display = "none";
            }, 100); // 500ms ka delay
        });

        megaMenu.addEventListener("mouseleave", function () {
            hideTimeout = setTimeout(() => {
                megaMenu.style.display = "none";
            }, 100); // 500ms ka delay
        });
    });
});
